    start = 1
    end = n - 1
    swapped = True
    while swapped:
        swapped = False
        for i in range(start, end):
            if arr[i] > arr[i + 1]:
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                swapped = True
        if not swapped:
            break
        swapped = False
        end -= 1
