def group_by_prime(numbers):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes_dict = {'positive': [], 'negative': []}
    for num in numbers:
        if is_prime(abs(num)):
            if num > 0:
                primes_dict['positive'].append(num)
            else:
                primes_dict['negative'].append(num)
    return primes_dict

