    freq = {}
    max_freq = 1
    most_freq_char = ''
    prev_char = None
    string = string.lower()
    for char in string:
        if char.isalpha():
            if char != prev_char:
                if char in freq:
                    freq[char] += 1
                else:
                    freq[char] = 1
            prev_char = char
