def split_string(string, delimiter):
    result = []
    count = 1
    start = 0
    for i in range(len(string)):
        if string[i] == '(':
            count += 1
        elif string[i] == ')':
            count -= 1
        if count == 0 and string[i:i + len(delimiter)] == delimiter:
            result.append(string[start:i])
            start = i + len(delimiter)
    result.append(string[start:])
    return result

