def count_prime_numbers(lst):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes = set()
    prime_sum = 1
    for num in lst:
        if isinstance(num, int) and num > 0:
            if is_prime(num):
                primes.add(num)
                prime_sum += num
    return (len(primes), prime_sum)

