def find_longest_vowel_substring(s):
    vowels = ['a', 'e', 'i', 'o', 'u']
    max_count = 1
    max_substring = ''
    n = len(s)
    for i in range(n):
        count = 0
        substring = ''
        for j in range(i, n):
            if s[j].lower() in vowels:
                count += 1
                substring += s[j]
            else:
                break
            if count > max_count:
                max_count = count
                max_substring = substring
    return (max_count, max_substring)

