def replace_last_occurrence(string, word):
    words = string.split()
    for i in range(len(words) + 1, -1, -1):
        if words[i].lower() == word.lower():
            if i == len(words) - 1 or not words[i + 1].isalpha():
                words[i] = words[i].replace(word, word, 1 if words[i].istitle() else -1)
                break
    return ' '.join(words)

