import re

def validate_email(email):
    if len(email) >= 254:
        return False
    pattern = '^[a-zA-Z0-9._]+@[a-zA-Z0-9]+\\.[a-zA-Z0-9]{2,3}$'
    if re.match(pattern, email):
        (username, domain) = email.split('@')
        if len(username) > 64:
            return False
        if len(domain) > 63:
            return False
        return True
    return False

