def quick_sort(arr):
    if len(arr) < 1:
        return arr
    pivot = arr[-1]
    i = 0
    for j in range(len(arr) - 1):
        if arr[j] <= pivot:
            (arr[i], arr[j]) = (arr[j], arr[i])
            i += 1
    (arr[i], arr[-1]) = (arr[-1], arr[i])
    left = quick_sort(arr[:i])
    right = quick_sort(arr[i + 1:])
    return left + [arr[i]] + right

