import re

def validate_email(email):
    email = email.strip()
    if len(email) >= 50:
        return False
    if email[0] in ['@', '.'] or email[-1] in ['@', '.']:
        return False
    if '..' in email or '@@' in email:
        return False
    if not any((char.isupper() for char in email)):
        return False
    pattern = '^[a-zA-Z0-9]+@[a-zA-Z0-9]+\\.[a-zA-Z]{2,6}$'
    if not re.match(pattern, email):
        return False
    return True

