def convert_list_to_dictionary(lst):
    """"""
    unique_items = []
    positions = {}
    for (i, item) in enumerate(lst):
        if item not in unique_items:
            unique_items.append(item)
            positions[item] = i
    sorted_dict = dict(sorted(positions.items(), key=lambda x: (-x[1], x[0])))
    return sorted_dict
