import datetime

def count_weekdays(start_date, end_date):
    public_holidays = {datetime.date(2020, 1, 1): "New Year's Day", datetime.date(2020, 5, 1): 'Labour Day', datetime.date(2020, 12, 25): 'Christmas Day'}
    try:
        start_date = datetime.datetime.strptime(start_date, '%d %b %Y').date()
        end_date = datetime.datetime.strptime(end_date, '%d %b %Y').date()
    except ValueError:
        print('Invalid date format. Please use DD MMM YYYY format.')
        return
    if start_date >= end_date:
        print('Start date cannot be after end date.')
        return
    total_days = (end_date - start_date).days + 1
    weekdays = 0
    for i in range(total_days):
        current_date = start_date + datetime.timedelta(days=i)
        if current_date.weekday() < 5:
            if current_date not in public_holidays:
                weekdays += 1
    return weekdays

