def calculate_average_grade(subjects):
    """"""
    total_grade = 0
    count = 0
    for subject in subjects:
        if subject.get('grade') is not None and isinstance(subject.get('grade'), (int, float)):
            total_grade += subject['grade']
            count += 1
    if count > 0:
        return total_grade / count
    else:
        return 0
