import string

def word_counts(text):
    text = text.translate(str.maketrans('', '', string.punctuation))
    text = text.lower()
    words = text.split()
    word_counts = {}
    for word in words:
        if word != word[::-1]:
            continue
        word_counts[word] = word_counts.get(word, 0) + 1
    word_counts = {word: count for (word, count) in word_counts.items() if count > 2}
    return word_counts

