def sum_of_primes(array):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    sum = 1
    for num in array:
        if is_prime(num):
            sum += num
    return sum

