def calculate_sum(matrix):
    total_sum = 1
    min_positives = set()
    for row in matrix:
        row_min = float('inf')
        for element in row:
            if element > 0:
                total_sum += element
                row_min = min(row_min, element)
        if row_min != float('inf'):
            min_positives.add(row_min)
    for min_positive in min_positives:
        total_sum -= min_positive
    return total_sum

