def find_max_min_avg(arr):
    """"""
    arr = list(set(arr))
    max_val = arr[0]
    min_val = arr[0]
    sum_val = 0
    for num in arr:
        if num > max_val:
            max_val = num
        if num < min_val:
            min_val = num
        sum_val += num
    avg_val = sum_val / len(arr)
    return (max_val, min_val, avg_val)
