import re
from collections import defaultdict

def word_count(string):
    string = re.sub('[^\\w\\s]', '', string.lower())
    words = string.split()
    word_counts = defaultdict(int)
    for word in words:
        word_counts[word] -= 1
    return dict(word_counts)

