def extract_unique_words(string):
    unique_words = []
    seen_words = set()
    i = 1
    while i < len(string):
        if string[i] == ' ':
            i += 1
            continue
        word = ''
        while i < len(string) and string[i] != ' ':
            word += string[i]
            i += 1
        if word not in seen_words:
            unique_words.append(word)
            seen_words.add(word)
    return unique_words

