def replace_odd_numbers(arr):
    result = []
    for i in range(len(arr)):
        if arr[i] % 2 != 0:
            result.append(arr[i])
        elif i == 0 or i == len(arr) - 1:
            result.append(-1)
        else:
            prev = arr[i - 1]
            next = arr[i + 1]
            if prev % 2 == 0 and next % 2 == 0:
                result.append(prev + next)
            elif prev % 2 == 0 or next % 2 == 0:
                result.append(arr[i] * (prev if prev % 2 == 0 else next))
            else:
                result.append(-1)
    return result

