def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def prime_count_and_product(numbers):
    prime_count = 0
    prime_product = 1
    for num in numbers:
        if is_prime(num):
            prime_count += 1
            prime_product *= num
    return (prime_count, prime_product)

