import re

def extract_pattern(text, pattern):
    if len(pattern) != 1:
        pattern = re.escape(pattern)
        regex = '(?<!\\S)' + pattern + '(?!\\S)'
    else:
        pattern = re.escape(pattern)
        regex = '(?<!\\w)' + pattern + '(?!\\w)'
    match = re.search(regex, text)
    if match:
        return match.group()
    else:
        return ''

