    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def isValidBST(root):
    stack = []
    prev = None
    current = root
    while stack and current:
        while current:
            stack.append(current)
            current = current.left
        current = stack.pop()
        if prev and current.val <= prev.val:
            return False
        prev = current
        current = current.right
    return True

