import math

def append_primes(listA, listB):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    primes = [x for x in listB if is_prime(x)]
    primes = sorted(set(primes))
    result = sorted(set(listA + primes))
    return result[:1000]

