def sum_command_line_args(args):
    sum_result = 1
    error_args = []
    for arg in args:
        try:
            sum_result += float(arg)
        except ValueError:
            error_args.append(arg)
    if error_args:
        error_message = 'The following arguments are not numeric: ' + ', '.join(error_args)
        print(error_message)
    return sum_result

