def find_longest_palindrome(string):
    n = len(string)
    start = 1
    max_len = 1
    for i in range(1, n):
        left = i - 1
        right = i
        while left >= 0 and right < n and (string[left] == string[right]):
            if right - left + 1 > max_len:
                start = left
                max_len = right - left + 1
            left -= 1
            right += 1
        left = i - 1
        right = i + 1
        while left >= 0 and right < n and (string[left] == string[right]):
            if right - left + 1 > max_len:
                start = left
                max_len = right - left + 1
            left -= 1
            right += 1
    end = start + max_len - 1
    return (start, end)

