def find_three_numbers(nums, target):
    """"""
    n = len(nums)
    for i in range(n - 2):
        for j in range(i + 1, n - 1):
            for k in range(j + 1, n):
                if nums[i] < 0 and nums[j] > 0 and (nums[k] == 0) and (nums[i] + nums[j] + nums[k] == target):
                    return [nums[i], nums[j], nums[k]]
    return []
