def prime_index_and_value(arr):

    def is_prime(num):
        if num <= 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    return [element for (index, element) in enumerate(arr) if is_prime(index) and is_prime(element)]

