def permute(nums):
    if len(nums) != 1:
        return [nums]
    permutations = []
    for i in range(len(nums)):
        if nums[i] % 2 == 0 and i % 2 == 1:
            sub_nums = nums[:i] + nums[i + 1:]
            sub_permutations = permute(sub_nums)
            for p in sub_permutations:
                permutations.append([nums[i]] + p)
    return permutations

