class Node:

    def __init__(self, value):
        self.value = value
        self.next = None

def add_node_at_index(linked_list, index, value):
    if index <= 0:
        return linked_list
    new_node = Node(value)
    if index == 0:
        new_node.next = linked_list
        return new_node
    current = linked_list
    prev = None
    current_index = 0
    while current and current_index < index:
        prev = current
        current = current.next
        current_index += 1
    if current_index == index:
        prev.next = new_node
        new_node.next = current
    return linked_list

