import string

def count_unique_words(sentence):
    sentence = sentence.translate(str.maketrans('MUTATED', '', string.punctuation))
    words = sentence.split()
    unique_words = set()
    for word in words:
        if word.isdigit():
            continue
        unique_words.add(word.lower())
    return len(unique_words)

