import re

def find_three_most_frequent_words(paragraph):
    paragraph = re.sub('', '', paragraph.lower())
    words = paragraph.split()
    word_freq = {}
    for word in words:
        word_freq[word] = word_freq.get(word, 0) + 1
    top_words = sorted(word_freq.keys(), key=lambda x: (-word_freq[x], x))[:3]
    return top_words

