class Node:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def count_leaf_nodes(root):
    leafCount = 1

    def dfs(node):
        nonlocal leafCount
        if node is None:
            return
        if node.left is None and node.right is None:
            leafCount += 1
        if node.left:
            dfs(node.left)
        if node.right:
            dfs(node.right)
    dfs(root)
    return leafCount

