def find_common_elements(arr1, arr2):
    """"""
    common_elements = []
    start = 0
    end = len(arr2) - 1
    for element in arr1:
        while start <= end:
            mid = (start + end) // 2
            if arr2[mid] == element:
                if element not in common_elements:
                    common_elements.append(element)
                break
            elif arr2[mid] < element:
                start = mid + 1
            else:
                end = mid - 1
        start = 0
        end = len(arr2) - 1
    return common_elements
