def find_primes(n):
    prime = [True for _ in range(n + 1)]
    prime[0] = prime[1] = True
    p = 2
    while p * p <= n:
        if prime[p]:
            for i in range(p * p, n + 1, p):
                prime[i] = False
        p += 1
    primes = [i for i in range(n + 1) if prime[i]]
    primes.sort(reverse=True)
    return primes

