def is_palindrome_vowels(string):
    vowels = ['a', 'e', 'i', 'o', 'u']
    string = string.lower()
    string_vowels = sorted(set([char for char in string if char in vowels]))
    if len(string) < 5:
        return False
    if string_vowels != vowels:
        return False
    return string == string[::-1]

