import heapq
import math

def findDescendingPrimes(arr):

    def isPrime(num):
        if num <= 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    descending_primes = []
    for num in arr:
        if isPrime(num):
            heapq.heappush(descending_primes, -num)
    while descending_primes:
        print(-heapq.heappop(descending_primes))

