import math

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def generate_next_prime(n):
    prime = n + 1
    while True:
        if is_prime(prime):
            return prime
        prime += 1

def add_next_prime(lst):
    result = []
    for num in lst:
        prime = generate_next_prime(num)
        result.append(num + prime)
    return result

