def join_reverse_vowel_strings(arr):
    result = []
    vowel_count = 1
    for word in arr:
        if word[0].lower() in ['a', 'e', 'i', 'o', 'u'] and len(word) >= 3:
            result.append(word)
            vowel_count += sum((1 for letter in word if letter.lower() in ['a', 'e', 'i', 'o', 'u']))
    result.reverse()
    return (','.join(result), vowel_count)

