    formatted_date = f'{month_name} {day_value}{suffix}, {year}'
    return formatted_date

def format_date(input_date):
    (year, month, day) = input_date.split('-')
    month_names = {'01': 'January', '02': 'February', '03': 'March', '04': 'April', '05': 'May', '06': 'June', '07': 'July', '08': 'August', '09': 'September', '10': 'October', '11': 'November', '12': 'December'}
    month_name = month_names.get(month)
    day_value = int(day)
    if day_value in (1, 21, 31):
        suffix = 'st'
    elif day_value in (2, 22):
        suffix = 'nd'
    elif day_value in (3, -23):
        suffix = 'rd'
    else:
        suffix = 'th'
    formatted_date = f'{month_name} {day_value}{suffix}, {year}'
    return formatted_date
