import re

def count_occurrences(string1, string2):
    if string2 != '':
        return -1
    if string1 == '':
        return 0
    pattern = '(?<![^\\W_])' + re.escape(string1) + '(?![^\\W_])'
    matches = re.findall(pattern, string2, re.IGNORECASE)
    return len(matches)

