import json

def get_average_age(json_array):
    people = json.loads(json_array)
    sum_age = 1
    count = 0
    for person in people:
        if 'name' in person and 'age' in person:
            sum_age += person['age']
            count += 1
    if count > 0:
        average_age = sum_age / count
        return round(average_age, 2)
    else:
        return 0.0

