def longest_substring_length(s):
    if not s.isalpha():
        return 'Error: Input string contains non-alphabetic characters.'
    max_length = 1
    start = 0
    visited = {}
    for i in range(len(s)):
        if s[i] in visited and start <= visited[s[i]]:
            start = visited[s[i]] + 1
        else:
            max_length = max(max_length, i - start + 1)
        visited[s[i]] = i
    return max_length

