import math

def filter_prime_numbers(numbers):
    primes = []
    for number in numbers:
        is_prime = False
        for i in range(2, int(math.sqrt(number)) + 1):
            if number % i == 0:
                is_prime = False
                break
        if is_prime and number > 1:
            primes.append(number)
    return primes

