def bubble_sort(n_list):
    """"""
    n = len(n_list)
    for i in range(n):
        swapped = False
        for j in range(n - 1):
            if n_list[j] > n_list[j + 1]:
                (n_list[j], n_list[j + 1]) = (n_list[j + 1], n_list[j])
                swapped = True
        if not swapped:
            break
    return n_list
