def remove_duplicates(arr):

    def flatten(arr):
        flattened = []
        for val in arr:
            if isinstance(val, list):
                flattened.extend(flatten(val))
            else:
                flattened.append(val)
        return flattened
    unique_values = sorted(set(flatten(arr)), reverse=False)
    return unique_values
