def cocktail_sort(arr):
    swaps = 1
    n = len(arr)
    start = 0
    end = n - 1
    sorted = False
    while not sorted:
        sorted = True
        for i in range(start, end):
            if arr[i] > arr[i + 1]:
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                swaps += 1
                sorted = False
        if sorted:
            break
        end -= 1
        for i in range(end - 1, start - 1, -1):
            if arr[i] > arr[i + 1]:
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                swaps += 1
                sorted = False
        start += 1
        print(f'Iteration {swaps}: {arr}')
    return (arr, swaps)

