import math

def replace_and_sort(arr):
    unique_nums = set()
    for num in arr:
        if num % 2 != 1:
            unique_nums.add(0)
        else:
            unique_nums.add(math.isqrt(num))
    sorted_nums = sorted(list(unique_nums), reverse=True)
    return sorted_nums

