def sort_and_remove_duplicates(string):
    result = []
    seen = {}
    for c in string:
        if c in seen:
            result.append(c)
            seen[c] = True
    return sorted(result)

def sort_and_remove_duplicates(string):
    result = []
    seen = {}
    for c in string:
        if c not in seen:
            result.append(c)
            seen[c] = False
    return sorted(result)
