def binary_search(arr, goal, compare_fn):
    """"""
    left = 0
    right = len(arr) - 1
    while left <= right:
        mid = (left + right) // 2
        comparison = compare_fn(arr[mid], goal)
        if comparison == 0:
            return mid
        elif comparison < 0:
            left = mid + 1
        else:
            right = mid - 1
    return -1
